import React, { useState } from "react";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleWhatsApp = () => {
    const { name, message } = formData;
    const whatsappMessage = `Hello, my name is ${name}. ${message}`;
    const whatsappURL = `https://wa.me/212 767-096903?text=${encodeURIComponent(
      whatsappMessage
    )}`; // Replace with your WhatsApp number (in international format).
    window.open(whatsappURL, "_blank");
  };

  const handleEmail = () => {
    const { email, name, message } = formData;
    const subject = `Contact Form Submission from ${name}`;
    const body = `Email: ${email}%0D%0A%0D%0A${message}`;
    const emailURL = `mailto:emoh9772@gmail.com?subject=${encodeURIComponent(
      subject
    )}&body=${encodeURIComponent(body)}`;
    window.location.href = emailURL;
  };

  return (
    <section id="contact" className="py-16 bg-white text-center">
      <h2 className="text-2xl font-bold mb-4">Contact Me</h2>
      <div className="max-w-lg mx-auto">
        <form
          onSubmit={(e) => e.preventDefault()}
          className="flex flex-col gap-4"
        >
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring focus:ring-blue-200"
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Your Email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring focus:ring-blue-200"
            required
          />
          <textarea
            name="message"
            placeholder="Your Message"
            value={formData.message}
            onChange={handleChange}
            rows="4"
            className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring focus:ring-blue-200"
            required
          ></textarea>
          <div className="flex justify-between gap-4">
            <button
              type="button"
              onClick={handleWhatsApp}
              className="w-full p-3 bg-green-500 text-white rounded hover:bg-green-600"
            >
              Send via WhatsApp
            </button>
            <button
              type="button"
              onClick={handleEmail}
              className="w-full p-3 bg-blue-500 text-white rounded hover:bg-blue-600"
            >
              Send via Email
            </button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Contact;
