import React, { useState } from "react";

const ProjectDetails = ({ project, onClose }) => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    // Ajout de la classe 'dark' au body pour appliquer les styles Tailwind
    document.body.classList.toggle("dark", !isDarkMode);
  };

  if (!project) return null;

  return (
    <div
      role="dialog"
      aria-labelledby="project-title"
      aria-describedby="project-description"
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
    >
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg max-w-2xl w-full p-6 relative transform transition-transform duration-300 scale-95">
        {/* Bouton de fermeture */}
        <button
          aria-label="Close"
          className="absolute top-2 right-2 text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200"
          onClick={onClose}
        >
          &times;
        </button>

        {/* En-tête avec titre et icône de mode sombre */}
        <div className="flex justify-between items-center mb-4">
          <h2
            id="project-title"
            className="text-2xl font-bold text-gray-900 dark:text-gray-100"
          >
            {project.name}
          </h2>
          {/* Icône de mode sombre */}
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700"
            aria-label="Toggle Dark Mode"
          >
            {isDarkMode ? (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth={1.5}
                stroke="currentColor"
                className="w-6 h-6 text-gray-900 dark:text-gray-100"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M12 3v1.5M12 19.5V21M4.22 4.22l1.06 1.06M17.72 17.72l1.06 1.06M1.5 12H3m18 0h1.5M4.22 19.78l1.06-1.06M17.72 6.28l1.06-1.06M12 7.5a4.5 4.5 0 100 9 4.5 4.5 0 000-9z"
                />
              </svg>
            ) : (
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth={1.5}
                stroke="currentColor"
                className="w-6 h-6 text-gray-900 dark:text-gray-100"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M21.752 15.002A9.718 9.718 0 0112.002 21c-5.523 0-10-4.477-10-10 0-4.833 3.441-8.871 8-9.752"
                />
              </svg>
            )}
          </button>
        </div>

        {/* Description du projet */}
        <p
          id="project-description"
          className="text-gray-600 dark:text-gray-400 mb-4"
        >
          {project.description}
        </p>

        {/* Galerie de screenshots */}
        <div className="flex overflow-x-auto space-x-4 py-4 scrollbar-thin scrollbar-thumb-gray-400 scrollbar-track-gray-200">
          {project.screenshots?.length > 0 ? (
            project.screenshots.map((src, index) => (
              <img
                key={index}
                src={src}
                alt={`Screenshot ${index + 1}`}
                className="w-64 h-40 object-cover rounded-lg shadow-md"
              />
            ))
          ) : (
            <p className="text-gray-500 dark:text-gray-400">
              No screenshots available.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProjectDetails;
