import React, { useState } from "react";
import ProjectDetails from "./ProjectDetails";

const Projects = () => {
  const [selectedProject, setSelectedProject] = useState(null);

  const projects = [
    { name: "Electronic Library", description: "A platform to read, buy, and publish books." },
    { name: "Hardware Store Management", description: "An app to manage inventory and sales." },
    // Ajoutez d'autres projets ici...
  ];

  return (
    <section id="projects" className="py-16 bg-gray-50 dark:bg-gray-900 text-center">
      <h2 className="text-2xl font-bold mb-4 text-gray-900 dark:text-gray-100">Projects</h2>
      <div className="max-w-4xl mx-auto border border-gray-200 dark:border-gray-700 rounded-lg shadow-inner p-4 bg-white dark:bg-gray-800">
        <ul className="grid md:grid-cols-2 gap-8">
          {projects.map((project, index) => (
            <li key={index} className="border rounded-lg p-6 shadow-sm hover:shadow-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100">
              <h3 className="text-xl font-semibold mb-2">{project.name}</h3>
              <p className="text-gray-600 dark:text-gray-400 mb-4">{project.description}</p>
              <button className="text-blue-500 dark:text-blue-300 hover:underline">
                View Project
              </button>
            </li>
          ))}
        </ul>
      </div>
      {selectedProject && (
        <ProjectDetails project={selectedProject} onClose={() => setSelectedProject(null)} />
      )}
    </section>
  );
};

export default Projects;
