import React, { useState, useEffect } from "react";
import './index.css';
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Footer from "./components/Footer";
import Skills from "./components/Skills";
import ScrollToTop from "./components/ScrollToTop";
import { FaMoon, FaSun } from "react-icons/fa";

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  // Apply/remove the `dark` class on the <html> tag
  useEffect(() => {
    const root = document.documentElement;
    if (isDarkMode) {
      root.classList.add("dark");
    } else {
      root.classList.remove("dark");
    }
  }, [isDarkMode]);

  return (
    <div className="App bg-white dark:bg-gray-900 dark:text-gray-200 min-h-screen">
      {/* Header with Dark Mode Toggle */}
      <div className="flex justify-between items-center px-4 py-2 bg-gray-200 dark:bg-gray-800">
        <button
          onClick={toggleDarkMode}
          className="text-2xl p-2 rounded bg-gray-300 dark:bg-gray-700 text-yellow-500 dark:text-blue-300"
          aria-label="Toggle Dark Mode"
        >
          {isDarkMode ? <FaSun /> : <FaMoon />}
        </button>
      </div>

      {/* Main Content */}
      <main>
        <About />
        <Skills />
        <Projects />
        <Contact />
        <Footer />
      </main>

      {/* Scroll to Top Button */}
      <ScrollToTop />
    </div>
  );
}

export default App;
