import React from "react";
import { FaReact, FaNodeJs, FaLaravel } from "react-icons/fa";

const skills = [
  {
    name: "React",
    description:
      "Une bibliothèque JavaScript puissante pour construire des interfaces utilisateur modernes et dynamiques.",
    logo: <FaReact className="text-blue-500 text-4xl" />,
  },
  {
    name: "Laravel",
    description:
      "Un framework PHP robuste pour créer des applications web avec une architecture MVC.",
    logo: <FaLaravel className="text-red-500 text-4xl" />,
  },
  {
    name: "Node.js",
    description:
      "Un environnement d'exécution JavaScript côté serveur pour créer des applications rapides et évolutives.",
    logo: <FaNodeJs className="text-green-500 text-4xl" />,
  },
];

const Skills = () => {
  return (
    <section id="skills" className="py-16 bg-white">
      <h2 className="text-2xl font-bold text-center mb-8">My Skills</h2>
      <div className="grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="bg-white shadow-md p-6 rounded-lg text-center hover:shadow-lg transition-shadow duration-300"
          >
            <div className="mb-4">{skill.logo}</div>
            <h3 className="text-xl font-semibold mb-2">{skill.name}</h3>
            <p className="text-gray-600">{skill.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
  
};

export default Skills;
