import React, { useState, useEffect, useRef } from "react";
import { FaBars, FaTimes, FaMoon, FaSun } from "react-icons/fa";

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [logoText, setLogoText] = useState("Visionary Code");
  const [isDarkMode, setIsDarkMode] = useState(false);
  const menuRef = useRef(null);

  const toggleMenu = () => setMenuOpen(!menuOpen);

  const closeMenu = () => setMenuOpen(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setLogoText((prevText) =>
        prevText === "Visionary Code" ? " Code" : "Visionary Code"
      );
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  // Apply dark mode
  useEffect(() => {
    const root = window.document.documentElement;
    if (isDarkMode) {
      root.classList.add("dark");
    } else {
      root.classList.remove("dark");
    }
  }, [isDarkMode]);

  const toggleDarkMode = () => setIsDarkMode((prev) => !prev);

  return (
    <header className="p-4 flex justify-between items-center z-50">
      <h1 className="text-xl font-bold text-blue-400">{logoText}</h1>

      <nav className="hidden md:flex space-x-4">
        <a href="#about" className="hover:text-gray-400">
          About
        </a>
        <a href="#skills" className="hover:text-gray-400">
          Skills
        </a>
        <a href="#projects" className="hover:text-gray-400">
          Projects
        </a>
        <a href="#contact" className="hover:text-gray-400">
          Contact
        </a>
      </nav>

      <div className="flex items-center space-x-4">
        {/* Dark Mode Toggle */}
        <button
          onClick={toggleDarkMode}
          className="text-2xl text-yellow-500 dark:text-blue-300"
          aria-label="Toggle Dark Mode"
        >
          {isDarkMode ? <FaSun /> : <FaMoon />}
        </button>

        {/* Mobile Menu Toggle */}
        <div className="md:hidden text-2xl cursor-pointer" onClick={toggleMenu}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </div>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <nav
          ref={menuRef}
          className="fixed top-0 right-0 h-full w-2/3 bg-white dark:bg-gray-800 shadow-lg transform md:hidden"
        >
          <ul className="flex flex-col items-center justify-center h-full space-y-6 text-lg text-gray-800 dark:text-gray-200">
            <li>
              <a href="#about" className="hover:text-gray-400" onClick={closeMenu}>
                About
              </a>
            </li>
            <li>
              <a href="#skills" className="hover:text-gray-400" onClick={closeMenu}>
                Skills
              </a>
            </li>
            <li>
              <a href="#projects" className="hover:text-gray-400" onClick={closeMenu}>
                Projects
              </a>
            </li>
            <li>
              <a href="#contact" className="hover:text-gray-400" onClick={closeMenu}>
                Contact
              </a>
            </li>
          </ul>
        </nav>
      )}
    </header>
  );
};

export default Header;
